mapper.select:
SELECT NVL(MAX(freeno), #{freeno}) as freeno
FROM free
WHERE  freeno < #{freeno}

SELECT NVL(MIN(freeno), #{freeno}) as freeno
FROM free
WHERE  freeno > #{freeno}

mapper.update:
UPDATE free
SET freecnt = freecnt + 1 
WHERE freeno = #{freeno}

UPDATE free
SET freetitle = #{freetitle}, freecontent = #{freecontent}, freedate = sysdate
WHERE freeno = #{freeno}

mapper.select:
SELECT COUNT(freeno) as cnt
FROM free
WHERE freeno = #{freeno}  AND freepasswd = #{freepasswd}

SELECT COUNT(freeno) as cnt
FROM free
WHERE freeno = #{freeno}  AND memberno = #{memberno}

mapper.update:
UPDATE free
SET freelike = freelike + 1
WHERE freeno = #{freeno}

UPDATE free
SET freelike = freelike - 1
WHERE freeno = #{freeno}
