mapper.select:
SELECT count(goodno)
FROM freelike
WHERE freeno = #{freeno} AND memberno = #{memberno}

SELECT goodchk
FROM freelike
WHERE freeno = #{freeno} AND memberno = #{memberno}

mapper.update:
UPDATE freelike
SET goodchk = 1
WHERE freeno = #{freeno} AND memberno = #{memberno}

UPDATE freelike
SET goodchk = 0
WHERE freeno = #{freeno} AND memberno = #{memberno}
